import { useState, useEffect } from "react";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import './components'; // Import CSS file for styling

const Products = () => {
    const url = 'https://fakestoreapi.com/products';
    const [products, setProducts] = useState([]);

    const getDataProducts = async () => {
        const response = await fetch(url);
        const dataProduct = await response.json();
        setProducts(dataProduct);
    }

    useEffect(() => {
        getDataProducts();
    }, []);

    return (
        <div className="container">
            <h1 className="text-center mb-5">My Products</h1>
            <div className="row">
                {products.map((product) => (
                    <div className="col-lg-3 col-md-4 col-sm-6 mb-4" key={product.id}>
                        <CardProduct
                            title={product.title}
                            price={product.price}
                            description={product.description}
                            image={product.image}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
}

function CardProduct(props) {
    return (
        <Card className="h-100">
            <Card.Img variant="top" src={props.image} className="card-img-top" />
            <Card.Body>
                <Card.Title>{props.title}</Card.Title>
                <Card.Text>{props.description}</Card.Text>
                <Card.Text className="font-weight-bold">Price: ${props.price}</Card.Text>
                <Button variant="primary">View Details</Button>
            </Card.Body>
        </Card>
    );
}

export default Products;
